<?php require_once('InstaBatch.php');$host = 'localhost';$user = 'dev';$pass = 'Circle203';$db = 'dev';// To use InstaBatch to retrieve cached data, set DB details$batch = new Instabatch(array(	'db_server' => $host,	'db_user' => $user,	'db_pass' => $pass,	'db_catalog' => $db,));$tag = (isset($_GET['tag'])) ? urldecode($_GET['tag']) : 'circles';echo '<h1>InstaBatch</h1>';echo "<h2>Object Query for `$tag` </h2>";// Get cached Instagram data from DB for the tag$objs = $batch->getItems($tag);if($objs) {		echo count($objs) . " found.<br />";	foreach($objs as $obj){		$data = $obj['data'];		$std_res = $data['images']->standard_resolution->url;		echo "<img src='$std_res' />";		echo "<br />";			}}else{	echo "<br>No objects returned for tag `$tag`";	}